import React,{useState,useEffect} from 'react'
import axios from 'axios'
import AddStudent from './AddStudent'
import GradesStudent from './GradesStudents'


// TODO: add change student name button
const StudentList = () => {
  const [studentList,getStudents] = useState([])
  const [createStudent,triggerCreateStudent] = useState(false)

  useEffect(()=>{
      axios.get('http://localhost:8000/student/')
        .then(response=>{
          getStudents(response.data)
        })

    },[])
    const handleClick = (e) => {

    }
    return (
      <div>
        <p>{studentList.map(item=>{
          return(
            <div>
              <p>{item.id}</p>
              <h1>{item.name}</h1>
              <br />
              <button onClick={handleClick}>delete student</button>
              {/* 
              1. this here creates the class list
              2. delete student button
              */}
              <GradesStudent student_id = {item.id}/>
              <hr />
            </div>
          )
        })}</p>
        <br />
        <button onClick = {()=>triggerCreateStudent(true)}>New To-Do</button>
        <br />
        {/* this only happens after the click because it makes it true on click(triggerCreateStudent) */}
        <AddStudent trigger = {createStudent}/>
      </div>


  )
}

export default StudentList
