import React,{useState,useEffect} from 'react'
import axios from 'axios'
import AddStudent from './AddStudent'
import GetCourse from './GetCourse'
import AddClassGrade from './AddClassGrade'


const GradesStudent = (props) => {
  const [studentList,getStudents] = useState([])
  const [trigger,setTrigger] = useState(false)


  useEffect(()=>{
    axios.get(`http://localhost:8000/classgrades/${props.student_id}/`)
      .then(response=>{
        console.log('fetching... from grades')

        getStudents(response.data)
      })

  },[])
  return (
    <div>
      <h3>Class List</h3>
      {studentList.map(item=>{
        return(
          <div>
            <p>
              grade: {item.grade}, the class is 
              {/* 
              we need to be able to view the course name, and i use this here to get that course name
              1. course name
              */}
              <GetCourse 
              course_id = {item.course_profile}/>
            </p>

          </div>
        )
      })}
      {/** 
      here we need to 
      //TODO: trigger create enrollment
      //TODO: add a form of creating a new student
      */}
      <button onClick = {()=>setTrigger(true)}>create new class</button>
      <br />
      <AddClassGrade trigger = {trigger}/>
    </div>
  )
}

export default GradesStudent
